export const filterCoaches = (coaches, selectedCoaches) => {
  return coaches.filter((coach) => {
    const isCoachCategorySelected = Object.entries(
      selectedCoaches?.coachCategory
    ).some(
      ([coachCategory, isSelected]) =>
        isSelected &&
        coach?.specialization?.toLowerCase() === coachCategory?.toLowerCase()
    );
    return isCoachCategorySelected;
  });
};
